def select_file():
    name = askopenfilename(initialdir="C:/Users/",
                           filetypes =(("CSV File", "*.csv"),("Text File", "*.txt"),("All Files","*.*")),
                           title = "Choose a file.")
    set_file_name(name)
    #try:
        #with open(name,'r',newline = '') as UseFile:
            #print(UseFile.read())
    #except:
        #print("No file exists")
    set_file_name(name)
    return name
